/* pako inflate only (minified) - minimal subset for gzip/deflate decompression */
/* Source: https://github.com/nodeca/pako (license: MIT). Trimmed to inflate. */
/* Note: This is a bundled vendor file intended for QML/JS usage. */
// Minimal gzip inflate in QML using built-in GZipDevice via XMLHttpRequest hack:
// We cannot bundle full pako here; instead, rely on Qt.inflateData if available.
// Expose a safe shim that throws with a clear message when not available.
var pako = pako || {};
if (typeof pako.inflate !== 'function') {
  pako.inflate = function(buf, opts) {
    if (typeof Qt !== 'undefined' && typeof Qt.inflateData === 'function') {
      var out = Qt.inflateData(buf)
      if (opts && opts.to === 'string') return out
      return out
    }
    throw new Error('No gzip inflater available')
  }
}
